										<SolaceEnvironmentSelectChip
											name="environment"
											value={selectedEnvironment.environmentId}
											onChange={handleSetEnvironment}
											options={environmentOptions}
										>
											<ToggleWrapper>
												<SolaceToggle
													id="show_all"
													isOn={selectedEnvironment.showAllResources}
