props.value:
[
					{ title: t({ id: "id_cluster_manager", message: "Cluster Manager" }), link: ROUTER_SERVICES },
					{
						title: t({ id: "id_service_details", message: "Service Details" }),
						link: routeSetParams(ROUTER_SERVICES_DETAILS, { serviceId: service?.id as string, tab }),
						current: true,
						progress: hasOngoingOperation,
						progressTooltip: t({ id: "id_broker_progress", message: "The broker is busy with an update. Please wait." })
					}
				]

service && (
						<ServiceMenu
							service={service}
							view="details"
							setRenamingService={setRenamingService}
							setDeletingService={setDeletingService}
							setUserAccessService={setUserAccessService}
							meshId={servicesInMeshes.get(serviceId)?.id}
							hideOpenService
						/>
					)

service && [
						<SolaceButton
							key="open-broker-manager"
							variant="link"
							dense
							dataQa="launchBrokerManager"
							isDisabled={!allowBrokerRequests}
							href={(allowBrokerRequests && getBrokerWebUI()) || ""}
							onClick={openBrokerManager}
						>
							<Trans id="id_open_broker_manager">Open Broker Manager</Trans>
						</SolaceButton>
					]

selectedEnvironment ? (
							<SolaceEnvironmentSelectChip name="environment" value={selectedEnvironment.environmentId} onChange={handleSetEnvironment} options={environmentOptions}>
								<ToggleWrapper>
									<SolaceToggle
										id="show_all"
										isOn={selectedEnvironment.showAllResources}
										onChange={handleShowAllEnvs}
										name="show_all_envs"
										label={t({ id: "id_show_all_environments", message: "Show resources in all environments" })}
										helperText={t({ id: "id_show_all_environments_help", message: "Enable to manage resources across environments" })}
									/>
								</ToggleWrapper>
							</SolaceEnvironmentSelectChip>
						) : undefined

[
					{ title: t({ id: "id_cluster_manager", message: "Cluster Manager" }), link: ROUTER_SERVICES },
					{ title: t({ id: "id_compare_services", message: "Compare Services" }), link: ROUTER_SERVICES_COMPARE, current: true }
				]

[
					{ title: t({ id: "id_cluster_manager", message: "Cluster Manager" }), link: servicesPath },
					{
						title: title,
						link: missionControl?.createUI ? ROUTER_SERVICES_CREATE : ROUTER_SERVICES_CREATE_LEGACY,
						current: true
					}
				]
