showSubtext ? (
						<Box mt={theme.spacing(1)} width="35.5rem">
							<SolaceSelect
								required
								maxHeight="40vh"
								name="windowSelect"
								onChange={handleChange}
								disabled={sortedUpcomingWindows.length === 0}
								value={sortedUpcomingWindows.length > 0 ? selectedValue : "empty"}
								getOptionDisplayValue={(value) => handleOptionDisplayValue(value as string)}
								label={t({ id: "id_maintenance_select_upcoming", message: "Select an upcoming window in this environment" })}
							>
								{sortedUpcomingWindows.map(({ window, startTime, index }) => {
									const { fullDate, localStart, utcStart, offset } = getWindowMenuItems(startTime);

									return (
										<MenuItem key={window.id + fullDate} value={stringifyValue({ windowId: window.id || "", index })}>
											<SolaceGrid container justifyContent="space-between">
												<SolaceGrid item>
													<Trans id="id_maintenance_schedule_dropdown_time">
														{fullDate} {localStart} ({utcStart}
														<Super>{offset}</Super> UTC)
													</Trans>
												</SolaceGrid>
												<SolaceGrid item color={theme.palette.ux.secondary.text.wMain}>
													{t({ id: "id_maintenance_hour_duration", message: "{0}-hour duration", values: { 0: window?.durationInHours ?? 0 } })}
												</SolaceGrid>
											</SolaceGrid>
										</MenuItem>
									);
								})}
							</SolaceSelect>
							{selectedValue && (
								<TimeBox>
									<SolaceTypography>{renderUpgradeEstimate(selectedValue, windows)}</SolaceTypography>
								</TimeBox>
							)}
						</Box>
					) : undefined

showSubtext && (isLoading || upgradeReadinessOK) ? (
					<AvailibilityBox>
						<TextWithIcon>
							<SuccessIcon fill={theme.palette.ux.success.wMain} />
							<SolaceTypography variant="body1" ml="-0.5rem">
								{t({
									id: "id_auto_upgrade_queue",
									message: "Upgrade space is available. Your upgrade will begin within {0} minutes.",
									values: { 0: upgradeReadiness?.data?.jobExpiryInMinutes }
								})}
							</SolaceTypography>
						</TextWithIcon>
					</AvailibilityBox>
				) : !upgradeReadinessOK && !isLoading ? (
					<AvailibilityBox>
						<SolaceTypography>
							{t({
								id: "id_auto_upgrade_queue_not",
								message: "Upgrade Now is not currently available. Please try again later."
							})}
						</SolaceTypography>
					</AvailibilityBox>
				) : undefined
